$input = '';

$markers = [];
for ($i = 0; $i < strlen($input); $i++) {

    $recent = substr($input, max(0, $i - 13), 14);
    
    if (count(array_unique(str_split($recent))) === 14) {
        $markers[] = $i;
    }
}

$first_marker = $markers[0];

echo $first_marker + 1;


